function attendance_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('attendance');
	if(!$arrPerm['insert']) return false;

	$data = [
		'empID' => Request::lookup('empID', ''),
		'name' => Request::lookup('empID'),
		'date' => Request::dateComponents('date', '1'),
		'time_in' => parseCode('<%%creationTime%%>', true, true),
	];


	// hook: attendance_before_insert
	if(function_exists('attendance_before_insert')) {
		$args = [];
		if(!attendance_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('attendance', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('attendance', $recID, calculated_fields()['attendance']);

	// hook: attendance_after_insert
	if(function_exists('attendance_after_insert')) {
		$res = sql("SELECT * FROM `attendance` WHERE `attID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!attendance_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('attendance', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) attendance_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function attendance_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function attendance_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('attendance', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: attendance_before_delete
	if(function_exists('attendance_before_delete')) {
		$args = [];
		if(!attendance_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `attendance` WHERE `attID`='{$selected_id}'", $eo);

	// hook: attendance_after_delete
	if(function_exists('attendance_after_delete')) {
		$args = [];
		attendance_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='attendance' AND `pkValue`='{$selected_id}'", $eo);
}

function attendance_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('attendance', $selected_id, 'edit')) return false;

	$data = [
		'empID' => Request::lookup('empID', ''),
		'name' => Request::lookup('empID'),
		'date' => Request::dateComponents('date', ''),
	];

	// get existing values
	$old_data = getRecord('attendance', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: attendance_before_update
	if(function_exists('attendance_before_update')) {
		$args = ['old_data' => $old_data];
		if(!attendance_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'attendance', 
		backtick_keys_once($set), 
		['`attID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="attendance_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('attendance', $data['selectedID'], calculated_fields()['attendance']);

	// hook: attendance_after_update
	if(function_exists('attendance_after_update')) {
		$res = sql("SELECT * FROM `attendance` WHERE `attID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['attID'];
		$args = ['old_data' => $old_data];
		if(!attendance_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='attendance' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

