		$templateCode = str_replace('<%%VALUE(date)%%>', app_datetime($row['date']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(date)%%>', urlencode(app_datetime($urow['date'])), $templateCode);
		$templateCode = str_replace('<%%VALUE(time_in)%%>', time12(html_attr($row['time_in'])), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(time_in)%%>', urlencode(time12($urow['time_in'])), $templateCode);
		$templateCode = str_replace('<%%VALUE(time_out)%%>', time12(html_attr($row['time_out'])), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(time_out)%%>', urlencode(time12($urow['time_out'])), $templateCode);
		$templateCode = str_replace('<%%VALUE(day_of_week)%%>', safe_html($urow['day_of_week']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(day_of_week)%%>', urlencode($urow['day_of_week']), $templateCode);
		$templateCode = str_replace('<%%VALUE(hours_worked)%%>', safe_html($urow['hours_worked']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(hours_worked)%%>', urlencode($urow['hours_worked']), $templateCode);
		$templateCode = str_replace('<%%VALUE(is_late)%%>', safe_html($urow['is_late']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(is_late)%%>', urlencode($urow['is_late']), $templateCode);
		$templateCode = str_replace('<%%VALUE(early_out)%%>', safe_html($urow['early_out']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(early_out)%%>', urlencode($urow['early_out']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(attID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(attID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(empID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(empID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(date)%%>', '1', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(date)%%>', urlencode('1'), $templateCode);
		$templateCode = str_replace('<%%VALUE(time_in)%%>', '<%%creationTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(time_in)%%>', urlencode('<%%creationTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(time_out)%%>', time12(''), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(time_out)%%>', urlencode(time12('')), $templateCode);
		$templateCode = str_replace('<%%VALUE(day_of_week)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(day_of_week)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(hours_worked)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(hours_worked)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(is_late)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(is_late)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(early_out)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(early_out)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tempID_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'attendance_autofill.php?rnd1=$rnd1&mfk=empID&id=' + encodeURIComponent('".addslashes($row['empID'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'attendance_autofill.php?rnd1=$rnd1&mfk=empID&id=' + encodeURIComponent(AppGini.current_empID{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#empID$rnd1').prop('disabled', true); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm['insert'] || (($arrPerm['edit'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['edit'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['edit'] == 3)) ? "\$j('#empID$rnd1').prop('disabled', false); " : "\$j('#empID$rnd1').prop('disabled', true); ")." \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#empID_caption').length) \$j('#empID_caption').click(function() { empID_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('attendance');
	if($selected_id) {
		$jdata = get_joined_record('attendance', $selected_id);
		if($jdata === false) $jdata = get_defaults('attendance');
		$rdata = $row;
	}
	$templateCode .= loadView('attendance-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: attendance_dv
	if(function_exists('attendance_dv')) {
		$args = [];
		attendance_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}